import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import { Alert, Button, Container, Form, FormGroup, Input, Label } from 'reactstrap';
import { Editor } from "@tinymce/tinymce-react";
import "draft-js/dist/Draft.css";



class ArticleEdit extends Component {
  emptyItem = {
    title: "",
    content: "",
  };

  constructor(props) {
    super(props);
    this.state = {
      item: this.emptyItem,
      errorMessage: null,
      isCreate: false,
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  async componentDidMount() {
    this.state.isCreate = this.props.match.params.id === "new"; // are we editing or creating?
    if (!this.state.isCreate) {
      const response = await this.props.api.getById(this.props.match.params.id);
      const article = await response.json();
      this.setState({ item: article });
    }
  }

  handleChange(content, editor) {
    this.setState({ content });
  }

  async handleSubmit(event) {
    event.preventDefault();
    const { item, isCreate } = this.state;

    let result = isCreate
      ? await this.props.api.create(item)
      : await this.props.api.update(item);

    if (!result.ok) {
      this.setState({
        errorMessage: `Failed to ${isCreate ? "create" : "update"} record: ${
          result.status
        } ${result.statusText}`,
      });
    } else {
      this.setState({ errorMessage: null });
      this.props.history.push("/articles");
    }
  }

  render() {
    const { item, errorMessage, isCreate } = this.state;
    const title = <h2>{isCreate ? "Add Article" : "Edit Article"}</h2>;

    return (
      <div>
        {this.props.navbar}
        <Container style={{ textAlign: "left" }}>
          {title}
          {errorMessage ? <Alert color="warning">{errorMessage}</Alert> : null}
          <Form onSubmit={this.handleSubmit}>
            <div className="row">
              <FormGroup className="col-md-8 mb-3">
                <Label for="name">Title</Label>
                <Input
                  type="text"
                  name="title"
                  id="name"
                  value={item.title || ""}
                  onChange={this.handleChange}
                  autoComplete="name"
                />
                <Editor
                  value={this.state.content}
                  init={{
                    height: 500,
                    menubar: false,
                  }}
                  onEditorChange={this.handleChange}
                />
              </FormGroup>
              <FormGroup className="col-md-4 mb-3">
                <Label for="phone">Content</Label>
                <Input
                  type="text"
                  name="content"
                  id="phone"
                  value={item.content || ""}
                  onChange={this.handleChange}
                  autoComplete="phone"
                />
              </FormGroup>
            </div>
            <FormGroup>
              <Button color="primary" type="submit">
                Save
              </Button>{" "}
              <Button color="secondary" tag={Link} to="/coffee-shops">
                Cancel
              </Button>
            </FormGroup>
          </Form>
        </Container>
      </div>
    );
  }
}

export default withRouter(ArticleEdit);
