import React, { Component } from "react";
import { Alert, Button } from "reactstrap";
import { Link } from "react-router-dom";
import ReactHtmlParser, {
  processNodes,
  convertNodeToElement,
  htmlparser2,
} from "react-html-parser";

const Article = (props) => (
  <div className="article-container p-2 m-2 d-flex flex-column">
    <h3>{props.name}</h3>
    <div className="article-body">
      <div className="bg-white flex flex-col subtitle-container">
        <div className="bg-black">
          <br />
          <br />
          <h2 className="text-white">{ReactHtmlParser(props.title)}</h2>
          <br />
        </div>
        <div className="blog p-10">{ReactHtmlParser(props.content)}</div>
      </div>
    </div>
    <div className="article-footer">
      <Button
        classname="w-full"
        color="secondary"
        tag={Link}
        to={"/articles/" + props.id}
      >
        Edit
      </Button>
      <Button color="danger" onClick={() => props.remove(props.id)}>
        Delete
      </Button>
      <Button color="success" tag={Link} to="/articles/new">
        Add New
      </Button>
    </div>
  </div>
);

class ArticlesList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      isLoading: true,
      errorMessage: null,
    };
    this.remove = this.remove.bind(this);
  }

  async componentDidMount() {
    this.setState({ isLoading: true });
    const response = await this.props.api.getAll();
    if (!response.ok) {
      this.setState({
        errorMessage: `Failed to load: ${response.status} ${response.statusText}`,
        isLoading: false,
      });
    } else {
      const body = await response.json();
      const articles = body;
      this.setState({
        articles: articles,
        isLoading: false,
        errorMessage: null,
      });
    }
  }

  async remove(id) {
    let response = await this.props.api.delete(id);
    if (!response.ok) {
      this.setState({
        errorMessage: `Failed to delete article: ${response.status} ${response.statusText}`,
      });
    } else {
      let updatedArticles = [...this.state.articles].filter((i) => i.id !== id);
      this.setState({ articles: updatedArticles, errorMessage: null });
    }
  }

  render() {
    const { articles, isLoading, errorMessage } = this.state;

    if (isLoading) {
      return <p>Loading...</p>;
    }

    return (
      <div>
        {this.props.navbar}
        <div className=" justify-content-between p-3">

          <br />
          <p className="text-white text-center">
            A place for me to keep track of my personal thoughts and my journey
            through life
          </p>
          <br />

        </div>
        {errorMessage ? (
          <div className="d-flex flex-row justify-content-center">
            <Alert color="warning" style={{ flex: 1, maxWidth: "80%" }}>
              {errorMessage}
            </Alert>
          </div>
        ) : null}
        <div className="d-flex flex-row flex-container flex-wrap justify-content-center">
          {articles.map((article) => (
            <Article
              {...article}
              remove={this.remove.bind(this)}
              key={article.id}
            />
          ))}
          {!articles || articles.length === 0 ? <p>No articles!</p> : null}
        </div>
      </div>
    );
  }
}

export default ArticlesList;
